import { TestBed } from '@angular/core/testing';
import { YourService } from './your-service'; // Import your service
import { of } from 'rxjs'; // Import of from rxjs

describe('YourComponent', () => {
  let yourService: YourService; // Declare a variable for your service

  beforeEach(() => {
    TestBed.configureTestingModule({
      providers: [
        YourComponent, // Include your component
        {
          provide: YourService, // Provide your service
          useValue: {
            yourObservableFunction: () => of(yourMockData) // Mock the function that returns an observable
          }
        }
      ]
    });

    yourService = TestBed.inject(YourService); // Inject your service
  });

  it('should do something', () => {
    // Your test code here
  });
});
