import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
//import { UserDataService } from 'src/app/service/user-data.service';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Group } from 'server/routes/groupModel';
import { ChangeDetectorRef } from '@angular/core';

declare var $: any;

const httpOptions = {
  headers: new HttpHeaders({ 'Content-Type': 'application/json' })
};

const BACKEND_URL = "http://localhost:3000";

@Component({
  selector: 'app-account',
  templateUrl: './account.component.html',
  styleUrls: ['./account.component.css']
})

export class AccountComponent implements OnInit {

  username: string;
  birthdate: string;
  age: number;
  allGroups: any;
  loggedInUser: any;
  users: any;
  currentUser: any; 
  currentUserGroups: any;
  selectedGroup: any;

  isUser: boolean = true;
  isAdmin: boolean = true;
  isSuperAdmin: boolean = true;

  constructor(private http: HttpClient, private router: Router) { 
    this.username = sessionStorage.getItem('username') ?? '';
    this.birthdate = sessionStorage.getItem('userbirthdate') ?? '';
    this.age = Number(sessionStorage.getItem('userage') ?? '0');
}

ngOnInit(): void {
  this.getUsers()
  this.getGroups()


//----------------------------------------------------------------
//Actual Code
  const storedUser = window.sessionStorage.getItem('current.user');
  if (storedUser) {

    this.loggedInUser = JSON.parse(storedUser);
    //.log(this.loggedInUser)
  }
//----------------------------------------------------------------

//----------------------------------------------------------------
//Testing Code
  // this.loggedInUser = {
  //   username: "super",
  //   birthdate: "2023-05-11",
  //   age: 0,
  //   email: "superEmail",
  //   password: "123",
  //   pwdconfirm: "123",
  //   role: 3,
  //   group: [],
  //   valid: true
  // }

  // this.loggedInUser = {
  //   username: "admin",
  //   birthdate: "2023-05-11",
  //   age: 0,
  //   email: "superEmail",
  //   password: "123",
  //   pwdconfirm: "123",
  //   role: 2,
  //   group: [],
  //   valid: true
  // }

  // this.loggedInUser = {
  //   username: "user",
  //   birthdate: "2023-05-11",
  //   age: 0,
  //   email: "superEmail",
  //   password: "123",
  //   pwdconfirm: "123",
  //   role: 1,
  //   group: [],
  //   valid: true
  // }

  if (this.loggedInUser.role === 1){
    this.isUser = true
    this.isAdmin = false
    this.isSuperAdmin = false
  }else if (this.loggedInUser.role === 2){
    this.isUser = false
    this.isAdmin = true
    this.isSuperAdmin = true
  }else if (this.loggedInUser.role === 3){
    this.isUser = false
    this.isAdmin = false
    this.isSuperAdmin = true
  }
}
//----------------------------------------------------------------

getGroups(){
  this.http.post(BACKEND_URL + "/all-groups", httpOptions)
  .subscribe(
      (data: any) => {
          if (data) {
            this.allGroups = data
            this.allGroups = data.filter((group: { valid: boolean; }) => group.valid === true);
            this.filterGroups(this.allGroups)
          } else {
              alert("no Data Soz");
          }
      },
      error => {console.error('There was an error:', error);}
  );
}

getUsers(){
  this.http.post(BACKEND_URL + "/all-users", httpOptions)
  .subscribe(
      (data: any) => {
          if (data) {
            this.users = data
            this.filterUser(this.users)
            //console.log(this.users)
          } else {
              alert("no Data Soz");
          }
      },
      error => {console.error('There was an error:', error);}
  );
}

filterGroups(group: any){
  console.log(group)
  console.log(this.loggedInUser.username)
  this.currentUserGroups = group.filter((g: { members: string | any[]; }) => g.members.includes(this.loggedInUser.username));
}

filterUser(users: any[]) {
  const matchedUser = users.find(u => u.username === this.loggedInUser.username);

  if (matchedUser) {
      this.currentUser = matchedUser;
      //console.log(this.currentUser)
      console.log('Match found and assigned to this.currentUser!');
  } else {
      console.log('No match found.');
  }
}


  logout() {
    // Clear session storage
    sessionStorage.clear();

    // Redirect to login page
    this.router.navigateByUrl('/login');
  }

  openUsers() {
    // Redirect to users page
    this.router.navigateByUrl('/users');
  }
  openGroups() {

    // Redirect to groups page
    this.router.navigateByUrl('/groups');
  }

  onGroupCardClick(group:any ){
    this.selectedGroup = group;
    console.log("card clicked")
    //.log(group)
    //this.router.navigate(['/channels', group]);

    this.router.navigate(['/channels'], { queryParams: { yourKey: JSON.stringify(group) }})
  }
}
<!DOCTYPE html>
<html lang="en">

<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <link href="https://stackpath.bootstrapcdn.com/bootstrap/4.5.2/css/bootstrap.min.css" rel="stylesheet">
    <title>Dashboard</title>
</head>

<body>

    <div class="container-fluid mt-4">
        <div class="row">

            <!-- Sidebar Navigation -->
            <div class="col-3 bg-light border-right vh-100">
                <div class="d-flex flex-column align-items-center pt-5">
                    <!-- Profile Image -->
                    <img src="https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_1280.png" class="img-fluid rounded-circle mb-4" alt="Profile Image" style="width: 120px; height: 120px;">

                    <!-- Edit Profile Button -->
                    <a routerLink="/profile" class="btn btn-info mb-2">Edit Profile</a>

                    <!-- Groups Button -->
                    <button *ngIf="isSuperAdmin" (click)="openUsers()"class="btn btn-secondary mb-2">Users</button>

                    <button (click)="openGroups()" class="btn btn-secondary mb-2">Groups</button>

                    <!-- Logout Button -->
                    <button (click)="logout()" class="btn btn-danger">Logout</button>
                </div>
            </div>

            <!-- Group Cards -->
            <div *ngIf="isUser || isAdmin " class="col-9">
                <div class="row justify-content-center"> <!-- Center the cards -->
                    <div class="col-md-3 mx-2 my-2" *ngFor="let group of currentUserGroups"> <!-- Spacing between cards -->
                        <div class="card square-card" (click)="onGroupCardClick(group)">
                            <div class="card-body d-flex flex-column">
                                <h5 class="card-title mt-auto">{{ group.groupName }}</h5>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            <!-- Group Cards -->
            <div *ngIf="isSuperAdmin" class="col-9">
                <div class="row justify-content-center"> <!-- Center the cards -->
                    <div class="col-md-3 mx-2 my-2" *ngFor="let group of allGroups"> <!-- Spacing between cards -->
                        <div class="card square-card" (click)="onGroupCardClick(group)">
                            <div class="card-body d-flex flex-column">
                                <h5 class="card-title mt-auto">{{ group.groupName }}</h5>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>


    <script src="https://code.jquery.com/jquery-3.5.1.slim.min.js"></script>
    <script src="https://stackpath.bootstrapcdn.com/bootstrap/4.5.2/js/bootstrap.min.js"></script>
</body>

</html>
