import { TestBed, ComponentFixture, async } from '@angular/core/testing';
import { GroupsComponent } from './groups.component';
import { HttpClientTestingModule, HttpTestingController } from '@angular/common/http/testing';
import { RouterTestingModule } from '@angular/router/testing';

describe('GroupsComponent', () => {
    let component: GroupsComponent;
    let fixture: ComponentFixture<GroupsComponent>;
    let httpMock: HttpTestingController;



    beforeEach(async(() => {
        TestBed.configureTestingModule({
            imports: [HttpClientTestingModule, RouterTestingModule],
            declarations: [GroupsComponent]
        }).compileComponents();

        fixture = TestBed.createComponent(GroupsComponent);
        component = fixture.componentInstance;
        httpMock = TestBed.inject(HttpTestingController);
        fixture.detectChanges();
    }));

    it('should remove a user from a group and its channels', () => {
        const mockGroup = {
            members: ['user1', 'user2'],
            channels: {
                channel1: ['user1', 'user2'],
                channel2: ['user1']
            }
        };

        component.loggedInUser = { username: 'user1' };

        component.leaveGroup(mockGroup);

        const req = httpMock.expectOne(`${component.BACKEND_URL}/update-groups`);
        expect(req.request.method).toBe('POST');
        req.flush({}); // mock a successful response

        expect(mockGroup.members).toEqual(['user2']);
        expect(mockGroup.channels.channel1).toEqual(['user2']);
        expect(mockGroup.channels.channel2).toEqual([]);
    });

    it('should save a group if it is unique', () => {
        component.isUniqueGroup = true;
        component.loggedInUser = { username: 'user1' };
        component.newGroupName = 'TestGroup';
        component.groupChannels = ['channel1', 'channel2'];
        component.newGroupID = '1234';

        component.saveGroup();

        const req = httpMock.expectOne(`${component.BACKEND_URL}/update-groups`);
        expect(req.request.method).toBe('POST');
        req.flush({}); // mock a successful response

        expect(component.group.groupName).toEqual('TestGroup');
        expect(component.group.members).toEqual(['user1']);
        expect(component.group.channels.channel1).toEqual([]);
        expect(component.group.channels.channel2).toEqual([]);
    });

    it('should not save a group if it is not unique', () => {
        component.isUniqueGroup = false;
        component.saveGroup();

        const reqs = httpMock.match(`${component.BACKEND_URL}/update-groups`);
        expect(reqs.length).toBe(0); // Expect no HTTP requests to be made
    });

    afterEach(() => {
        httpMock.verify(); // Ensure that no requests are outstanding
    });
});
