import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { HttpClient, HttpHeaders } from '@angular/common/http';

const BACKEND_URL = "http://localhost:3000";
const httpOptions = {
  headers: new HttpHeaders({ 'Content-Type': 'application/json' })
};

@Component({
  selector: 'app-create-user',
  templateUrl: './create-user.component.html',
  styleUrls: ['./create-user.component.css']
})
export class CreateUserComponent implements OnInit {

  user = {
    username: '',
    email: '',
    password: '',
    password2: '',
    birthday: '',
    roles: [1], // default values
    groups: [1] // default values
  };

  constructor(private router: Router, private http: HttpClient) {}

  ngOnInit(): void {}

  onCreate() {
    if (this.user.password !== this.user.password2) {
      alert('Passwords do not match!');  // Notify the user
      return;
    }

    // Removing the 'password2' field as we won't send it to the server
    const userToSend = { ...this.user };
    // delete userToSend.password2;

    // Now, send the user data to your backend
    this.http.post(BACKEND_URL + "/create-user", userToSend, httpOptions).subscribe(
      (response: any) => {
        // Assuming your backend returns { success: true } upon a successful addition
        if (response && response.success) {
          // Save to session storage
          sessionStorage.setItem("username", userToSend.username);
          sessionStorage.setItem("email", userToSend.email);
          sessionStorage.setItem("birthday", userToSend.birthday);

          // Navigate the user back to login
          this.router.navigateByUrl('login');
        } else {
          alert('Failed to create user.');
        }
      },
      error => {
        console.error('There was an error during user creation:', error);
      }
    );
  }
}
<div class="container mt-5">
    <h2 class="mb-4">Create Account</h2>
    <form (ngSubmit)="onCreate()">
        <div class="mb-3">
            <label for="username" class="form-label">Username</label>
            <input type="text" class="form-control" id="username" [(ngModel)]="user.username" name="username" required>
        </div>
        <div class="mb-3">
            <label for="password" class="form-label">Password</label>
            <input type="password" class="form-control" id="password" [(ngModel)]="user.password" name="password" required>
        </div>
        <div class="mb-3">
            <label for="password2" class="form-label">Confirm Password</label>
            <input type="password" class="form-control" id="password2" [(ngModel)]="user.password2" name="password2" required>
        </div>
        <div class="mb-3">
            <label for="email" class="form-label">Email</label>
            <input type="email" class="form-control" id="email" [(ngModel)]="user.email" name="email" required>
        </div>
        <div class="mb-3">
            <label for="birthday" class="form-label">Birthday</label>
            <input type="date" class="form-control" id="birthday" [(ngModel)]="user.birthday" name="birthday" required>
        </div>
        <input type="hidden" [(ngModel)]="user.roles" name="roles" value="[1]">
        <input type="hidden" [(ngModel)]="user.groups" name="groups" value="[1]">
        <button type="submit" class="btn btn-primary">Create Account</button>
    </form>
</div>
