import { ComponentFixture, TestBed } from '@angular/core/testing';
import { HttpClientTestingModule, HttpTestingController } from '@angular/common/http/testing';
import { FormsModule } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { of } from 'rxjs';
import { ChatComponent } from './chat.component';
import { UploadService } from '../service/upload.service';
import { throwError } from 'rxjs';
import { UploadResponse } from 'server/models/interfaces';



describe('ChatComponent', () => {
  let component: ChatComponent;
  let fixture: ComponentFixture<ChatComponent>;
  let uploadService: UploadService;
  let httpMock: HttpTestingController;


  beforeEach(() => {
    TestBed.configureTestingModule({
      declarations: [ChatComponent],
      imports: [HttpClientTestingModule, FormsModule],
      providers: [
        {
          provide: ActivatedRoute,
          useValue: { paramMap: of({ get: (id: string) => '1' }) }
        },
        UploadService
      ]
    });

    fixture = TestBed.createComponent(ChatComponent);
    component = fixture.componentInstance;
    uploadService = TestBed.inject(UploadService);
    httpMock = TestBed.inject(HttpTestingController);

    fixture.detectChanges();
  });

  afterEach(() => {
    httpMock.verify();
  });

  it('should create', () => {
    expect(component).toBeTruthy();
  });

  it('should call uploadAttachment and handle success', () => {
    const formData = new FormData();
    const mockResponse = { message: 'Upload successful' }; // Replace with your mock response data

    spyOn(uploadService, 'uploadAttachment').and.returnValue(of(mockResponse));

    component.uploadAttachment();

    expect(uploadService.uploadAttachment).toHaveBeenCalled();

    httpMock.expectOne("http://localhost:3000" + '/attachPhoto').flush(mockResponse);

    // Add your expectations for the component's behavior here
  });

  it('should handle uploadAttachment error', () => {
    const formData = new FormData();
    const mockError = { status: 500, statusText: 'Internal Server Error' }; // Replace with your mock error data

    spyOn(uploadService, 'uploadAttachment').and.returnValue(
      throwError(mockError) // Use throwError to create an observable that emits an error
    );

    component.uploadAttachment();

    expect(uploadService.uploadAttachment).toHaveBeenCalled();

    // Add your expectations for how the component handles the error here
  });
});
