import { TestBed, ComponentFixture, async } from '@angular/core/testing';
import { GroupsComponent } from './groups.component';
import { HttpClientTestingModule, HttpTestingController } from '@angular/common/http/testing';
import { RouterTestingModule } from '@angular/router/testing';

describe('GroupsComponent', () => {
    let component: GroupsComponent;
    let fixture: ComponentFixture<GroupsComponent>;
    let httpMock: HttpTestingController;

    beforeEach(async(() => {
        TestBed.configureTestingModule({
            imports: [HttpClientTestingModule, RouterTestingModule],
            declarations: [GroupsComponent]
        }).compileComponents();

        fixture = TestBed.createComponent(GroupsComponent);
        component = fixture.componentInstance;
        httpMock = TestBed.inject(HttpTestingController);
        fixture.detectChanges();
    }));

    it('should create the GroupsComponent', () => {
        expect(component).toBeTruthy();
    });

    it('should get groups names on initialization', () => {
        const mockGroupsNames = ['Group1', 'Group2', 'Group3'];

        component.ngOnInit();

        const req = httpMock.expectOne('http://localhost:3000/groups');
        expect(req.request.method).toBe('GET');
        req.flush(mockGroupsNames);

        expect(component.allGroupNames).toEqual(mockGroupsNames);
    });

    afterEach(() => {
        httpMock.verify(); // Ensure that no requests are outstanding
    });
});
