import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-channels',
  templateUrl: './channels.component.html',
  styleUrls: ['./channels.component.css']
})
export class ChannelsComponent implements OnInit {

  currentGroupObject: any;
  currentChannels: any;
  currentChannelskeys: any;
  loggedInUser:any; 

  constructor(private route: ActivatedRoute) { 
}

ngOnInit(): void {

  const storedUser = window.sessionStorage.getItem('current.user');
  if (storedUser) {

    this.loggedInUser = JSON.parse(storedUser);
  }

  this.loggedInUser.role = 1

  this.route.queryParams.subscribe(params => {
    let currentGroupString = params['yourKey'];

    // Check if the data is stringified object
    try {
        this.currentGroupObject = JSON.parse(currentGroupString);
        console.log(this.currentGroupObject);
    } catch (e) {
        console.error('Error parsing the object', e);
    }

    this.getchannelsKeys()

 });
}

getchannelsKeys() {
  this.currentChannelskeys = Object.keys(this.currentGroupObject.channels);
}

addUserToChannel(channel:any){
  console.log(channel)
  console.log(this.loggedInUser.username)
  console.log(this.currentGroupObject)



}

}
<div class="container mt-4">
    <div class="row">
        <div *ngFor="let channel of currentChannelskeys" class="col-12 mb-3">
            <div class="card">
                <div class="card-body d-flex justify-content-between align-items-center">
                    <span>{{channel}}</span>
                    <div>
                        <button class="btn btn-primary mr-2" (click)="addUserToChannel(channel)">Join Channel</button>
                        <button class="btn btn-secondary">Button 2</button>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
