                <input type="text" class="form-control" id="channelName" [(ngModel)]="newChannelName">
                <button type="button" class="btn btn-primary mt-2" (click)="addChannel('save')">Add Channel</button>
              </div>
            </div>
            </div>
          </div>
        </div>
