          this.numberOfRequests = this.groupsNeedApproval.length;
        } else if (this.loggedInUser.role == 3) {
          this.groupsNeedApproval = this.groups.filter(group => group.userRequests && group.userRequests.length > 0);
          this.numberOfRequests = this.groupsNeedApproval.length;
          this.groupsAdminNeedApproval = this.groups.filter(group => group.adminRequests && group.adminRequests.length > 0);
          this.numberOfAdminRequests = this.groupsAdminNeedApproval.length;
