    //   birthdate: "2023-05-11",
    //   age: 0,
    //   email: "superEmail",
    //   password: "123",
    //   pwdconfirm: "123",
    //   role: 2,
    //   group: [],
    //   valid: true
    // }
    // this.loggedInUser = {
    //   username: "user",
    //   birthdate: "2023-05-11",
    //   age: 0,
    //   email: "superEmail",
    //   password: "123",
    //   pwdconfirm: "123",
    //   role: 1,
    //   group: [],
    //   valid: true
    // }
    // this.loggedInUser = {
    //   username: "user2",
    //   birthdate: "2023-05-11",
    //   age: 0,
    //   email: "superEmail",
    //   password: "123",
    //   pwdconfirm: "123",
    //   role: 1,
    //   group: [],
    //   valid: true
    // }
    // this.loggedInUser = {
    //   username: "user3",
    //   birthdate: "2023-05-11",
    //   age: 0,
    //   email: "superEmail",
    //   password: "123",
    //   pwdconfirm: "123",
    //   role: 1,
    //   group: [],
    //   valid: true
    // }
    if (this.loggedInUser.role === 1) {
      this.isUser = true;
      this.isAdmin = false;
      this.isSuperAdmin = false;
    } else if (this.loggedInUser.role === 2) {
      this.isUser = false;
      this.isAdmin = true;
      this.isSuperAdmin = false;
    } else if (this.loggedInUser.role === 3) {
      this.isUser = false;
      this.isAdmin = false;
      this.isSuperAdmin = true;
    }
    this.getUsers();
    this.getGroups();
    this.http.get(BACKEND_URL + "/groups").subscribe(groupsNames => {
      this.allGroupNames = groupsNames;
    });
  }
  onGroupCardClick(group) {
    this.selectedGroup = group;
    console.log("card clicked");
    //.log(group)
    //this.router.navigate(['/channels', group]);
    this.router.navigate(['/channels'], {
      queryParams: {
        yourKey: JSON.stringify(group)
      }
    });
  }
  //UserRequests 
  requestButton(group) {
    console.log(this.loggedInUser.username);
    group.userRequests.push(this.loggedInUser.username);
    console.log(group);
    this.http.post(BACKEND_URL + "/update-groups", group).subscribe(response => {
      console.log('User details updated on the server.', response);
      //refesh User list 
      this.getGroups();
    }, error => {
      console.error('There was an error updating the user details on the server.', error);
      alert('Error updating profile. Please try again.');
    });
    this.buttonDisabledStates[group.groupID] = true;
  }
  addUsers(group) {
    console.log("Button 2 clicked");
  }
  onAddGroup() {
    console.log("Add Group button clicked");
    $('#addGroupModal').modal('show');
  }
  onGroupInput() {
    this.isUniqueGroup = !this.allGroupNames.includes(this.newGroupName);
    console.log(this.isUniqueGroup);
  }
  addChannel() {
    if (this.newChannelName && !this.groupChannels.includes(this.newChannelName)) {
      this.groupChannels.push(this.newChannelName);
      this.newChannelName = '';
    }
  }
  leaveGroup(group) {
    console.log(group);
    console.log(this.loggedInUser.username);
    // Remove user from group.members
    group.members = group.members.filter(member => member !== this.loggedInUser.username);
    // Loop through each channel in group.channels and remove the user
    for (const channelName in group.channels) {
      group.channels[channelName] = group.channels[channelName].filter(member => member !== this.loggedInUser.username);
    }
    this.http.post(BACKEND_URL + "/update-groups", group).subscribe(response => {
      //refesh User list 
      this.getGroups();
    }, error => {
      console.error('There was an error updating the user details on the server.', error);
      alert('Error updating profile. Please try again.');
    });
  }
  saveGroup() {
    //console.log(this.newGroupName)
    // console.log(this.groupChannels)
    this.GetNewGroupID();
    //console.log(this.newGroupID)
    let channelsObject = {};
    this.groupChannels.forEach(channel => {
      channelsObject[channel] = [];
    });
    if (this.isUniqueGroup === true) {
      this.group.groupID = this.newGroupID;
      this.group.groupName = this.newGroupName;
      this.group.createdBy = this.loggedInUser.username;
      this.group.groupAdmins = [this.loggedInUser.username];
      this.group.userRequests = [];
      this.group.members = [this.loggedInUser.username];
      this.group.channels = channelsObject;
      this.group.valid = true;
      //console.log(this.group)
      this.http.post(BACKEND_URL + "/update-groups", this.group).subscribe(response => {
        console.log('User details updated on the server.', response);
        //refesh User list 
        this.getGroups();
      }, error => {
        console.error('There was an error updating the user details on the server.', error);
        alert('Error updating profile. Please try again.');
      });
      this.closeModal("newGroup");
    } else {
      alert("Group already exists");
    }
  }
  closeModal(modalType) {
    if (modalType == "newGroup") {
      this.group = {
        groupID: 0,
        groupName: "",
        createdBy: this.loggedInUser.username,
        adminRequests: [],
        groupAdmins: [],
        userRequests: [],
        members: [],
        channels: {},
        valid: true
      };
      this.newGroupName = '';
      this.groupChannels = [];
      //console.log(this.group)
      $('#addGroupModal').modal('hide');
    }
    if (modalType == "requestApproval") {
      $('#approveRequests').modal('hide');
    }
    if (modalType == "requestAdminApproval") {
      $('#approveAdminRequests').modal('hide');
    }
  }
  getUsers() {
    this.http.post(BACKEND_URL + "/all-users", httpOptions).subscribe(data => {
      if (data) {
        this.users = data;
        //console.log(this.users)
      } else {
        alert("no Data Soz");
      }
    }, error => {
      console.error('There was an error:', error);
    });
  }
  getGroups() {
    this.http.post(BACKEND_URL + "/all-groups", httpOptions).subscribe(data => {
      if (data) {
        this.groups = data.filter(group => group.valid === true);
        // Groups created by loggedInUser
        this.myGroups = this.groups.filter(group => group.createdBy === this.loggedInUser.username || group.groupAdmins.includes(this.loggedInUser.username));
        // Groups that loggedInUser is a member of but not created by them
        this.joinedGroups = this.groups.filter(group => group.members.includes(this.loggedInUser.username) && group.createdBy !== this.loggedInUser.username && !group.groupAdmins.includes(this.loggedInUser.username));
        // Groups that loggedInUser has neither created nor joined
        this.otherGroups = this.groups.filter(group => group.createdBy !== this.loggedInUser.username && !group.members.includes(this.loggedInUser.username));
        // console.log(this.loggedInUser)
        // console.log(this.myGroups)
        // console.log(this.otherGroups)
        //if groupAdmin role filter requests to administered groups -- if superAdmin show all requests
        if (this.loggedInUser.role == 2) {
          this.groupsNeedApproval = this.groups.filter(group => group.userRequests && group.userRequests.length > 0 && group.groupAdmins.includes(this.loggedInUser.username));
          this.numberOfRequests = this.groupsNeedApproval.length;
        } else if (this.loggedInUser.role == 3) {
          this.groupsNeedApproval = this.groups.filter(group => group.userRequests && group.userRequests.length > 0);
          this.numberOfRequests = this.groupsNeedApproval.length;
          this.groupsAdminNeedApproval = this.groups.filter(group => group.adminRequests && group.adminRequests.length > 0);
          this.numberOfAdminRequests = this.groupsAdminNeedApproval.length;
        }
        //console.log(this.numberOfRequests)
        //this.allGroups = data
        //console.log(this.groups)
        //console.log(typeof(this.groups))
      } else {
        alert("no Data Soz");
      }
    }, error => {
      console.error('There was an error:', error);
    });
  }
  // updateGroup(){
  // }
  GetNewGroupID() {
    if (this.groups && this.groups.length > 0) {
      this.newGroupID = this.groups[this.groups.length - 1].groupID + 1;
    } else {
      this.newGroupID = 1;
    }
  }
  // getRequests(group: any){
  //     console.log(group)
  //       //this.groupsNeedApproval = this.groups.filter((group: { userRequests: string | any[]; }) => group.userRequests && group.userRequests.length > 0);
  //       //this.numberOfRequests=this.groupsNeedApproval.length
  // }
  showRequests() {
    this.getGroups(); // Assuming this method populates the 'this.groups' array
    // this.getRequests()
    $('#approveRequests').modal('show');
    if (this.groups.length > 0) {
      //this.noRequests=false
      this.groupsNeedApproval = this.groups.filter(group => group.userRequests && group.userRequests.length > 0);
    }
  }
  requestAdminButton(group) {
    //console.log(this.loggedInUser.username)
    group.adminRequests.push(this.loggedInUser.username);
    this.http.post(BACKEND_URL + "/update-groups", group).subscribe(response => {
      console.log('User details updated on the server.', response);
      //refesh User list 
      this.getGroups();
    }, error => {
      console.error('There was an error updating the user details on the server.', error);
      alert('Error updating profile. Please try again.');
    });
    this.buttonDisabledStates[group.groupID] = true;
  }
  deleteGroup(group) {
    console.log(group);
    group.valid = false;
    this.http.post(BACKEND_URL + "/update-groups", group).subscribe(response => {
      console.log('User details updated on the server.', response);
      //refesh User list 
      this.getGroups();
    }, error => {
      console.error('There was an error updating the user details on the server.', error);
      alert('Error updating profile. Please try again.');
    });
  }
  showAdminRequests() {
    this.getGroups(); // Assuming this method populates the 'this.groups' array
    // this.getRequests()
    $('#approveAdminRequests').modal('show');
    console.log(this.groups);
    // if(this.groups.length > 0){
    //   //this.noRequests=false
    //   this.groupsAdminNeedApproval = this.groups.filter((group: { adminRequests: string | any[]; }) => group.adminRequests && group.adminRequests.length > 0);
    // }
  }

  approveRequest(group, userRequest) {
    console.log(group);
    console.log(userRequest);
    if (group.userRequests.includes(userRequest)) {
      group.members.push(userRequest);
      group.userRequests.splice(group.userRequests.indexOf(userRequest), 1);
    }
    this.http.post(BACKEND_URL + "/update-groups", group).subscribe(response => {
      console.log('User details updated on the server.', response);
      //refesh User list 
      this.getGroups();
    }, error => {
      console.error('There was an error updating the user details on the server.', error);
      alert('Error updating profile. Please try again.');
    });
    //console.log(group);
  }

  approveAdminRequest(group, adminRequest) {
    console.log(group);
    console.log(adminRequest);
    if (group.adminRequests.includes(adminRequest)) {
      group.groupAdmins.push(adminRequest);
      group.adminRequests.splice(group.adminRequests.indexOf(adminRequest), 1);
    }
    console.log(group);
    this.http.post(BACKEND_URL + "/update-groups", group).subscribe(response => {
      console.log('User details updated on the server.', response);
      //refesh User list 
      this.getGroups();
    }, error => {
      console.error('There was an error updating the user details on the server.', error);
      alert('Error updating profile. Please try again.');
    });
  }
}
