import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
//import { UserDataService } from 'src/app/service/user-data.service';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Group } from 'server/routes/groupModel';
import { ChangeDetectorRef } from '@angular/core';

declare var $: any;

const httpOptions = {
  headers: new HttpHeaders({ 'Content-Type': 'application/json' })
};

const BACKEND_URL = "http://localhost:3000";

@Component({
  selector: 'app-account',
  templateUrl: './account.component.html',
  styleUrls: ['./account.component.css']
})

export class AccountComponent implements OnInit {

  username: string;
  birthdate: string;
  age: number;
  allGroups: any;
  loggedInUser : any;

  constructor(private http: HttpClient, private router: Router) { 
    this.username = sessionStorage.getItem('username') ?? '';
    this.birthdate = sessionStorage.getItem('userbirthdate') ?? '';
    this.age = Number(sessionStorage.getItem('userage') ?? '0');
}

ngOnInit(): void {
  this.getGroups()

  const storedUser = window.sessionStorage.getItem('current.user');
  if (storedUser) {

    this.loggedInUser = JSON.parse(storedUser);
  }


}

getGroups(){
  this.http.post(BACKEND_URL + "/all-groups", httpOptions)
  .subscribe(
      (data: any) => {
          if (data) {
            this.allGroups = data
            this.filterGroups(this.allGroups)
          } else {
              alert("no Data Soz");
          }
      },
      error => {console.error('There was an error:', error);}
  );
}

filterGroups(group: any){
console.log(group)
}


  logout() {
    // Clear session storage
    sessionStorage.clear();

    // Redirect to login page
    this.router.navigateByUrl('/login');
  }

  openUsers() {
    // Redirect to users page
    this.router.navigateByUrl('/users');
  }
  openGroups() {

    // Redirect to groups page
    this.router.navigateByUrl('/groups');
  }
}
<!DOCTYPE html>
<html lang="en">

<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <link href="https://stackpath.bootstrapcdn.com/bootstrap/4.5.2/css/bootstrap.min.css" rel="stylesheet">
    <title>Dashboard</title>
</head>

<body>

    <div class="container-fluid mt-4">
        <div class="row">

            <!-- Sidebar Navigation -->
            <div class="col-3 bg-light border-right vh-100">
                <div class="d-flex flex-column align-items-center pt-5">
                    <!-- Profile Image -->
                    <img src="https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_1280.png" class="img-fluid rounded-circle mb-4" alt="Profile Image" style="width: 120px; height: 120px;">

                    <!-- Edit Profile Button -->
                    <a routerLink="/profile" class="btn btn-info mb-2">Edit Profile</a>

                    <!-- Groups Button -->
                    <button (click)="openUsers()"class="btn btn-secondary mb-2">Users</button>

                    <button (click)="openGroups()" class="btn btn-secondary mb-2">Groups</button>

                    <!-- Logout Button -->
                    <button (click)="logout()" class="btn btn-danger">Logout</button>
                </div>
            </div>
        </div>
    </div>

    <script src="https://code.jquery.com/jquery-3.5.1.slim.min.js"></script>
    <script src="https://stackpath.bootstrapcdn.com/bootstrap/4.5.2/js/bootstrap.min.js"></script>
</body>

</html>
