<div class="container mt-4">
    <h2 class="text-center">Login</h2>

    <form>
        <div class="form-group">
            <label for="usernameEmail">Username by email:</label>
            <input type="email" [(ngModel)]="userpwd.username" name="usernameEmail" id="usernameEmail" class="form-control" placeholder="Enter email">
        </div>

        <div class="form-group">
            <label for="userPassword">User Password:</label>
            <input type="password" [(ngModel)]="userpwd.pwd" name="userPassword" id="userPassword" class="form-control" placeholder="Enter password">
        </div>
        <div class="text-center">
            <button class="btn btn-primary" (click)="loginfunc()">Login</button>
            <button class="btn btn-primary"(click)="creatfunc()">Create User</button>
        </div>
    </form>
</div>
