import { Component, OnInit } from '@angular/core';
//import { UserDataService } from 'src/app/service/user-data.service';
import { HttpClient, HttpHeaders } from '@angular/common/http';
declare var $: any;

const httpOptions = {
  headers: new HttpHeaders({ 'Content-Type': 'application/json' })
};

const BACKEND_URL = "http://localhost:3000";

@Component({
  selector: 'app-users',
  templateUrl: './users.component.html',
  styleUrls: ['./users.component.css']
})
export class UsersComponent implements OnInit {

  users: any;
  // superAdmin: number;
  // Admin: number;
  // User: number;
  selectedUser: any;
  selectedRole: number | undefined;

  loggedInUser : any;

  constructor(private http: HttpClient) { }

  ngOnInit(): void {

    const storedUser = window.sessionStorage.getItem('current.user');
    if (storedUser) {
      this.loggedInUser = JSON.parse(storedUser);
      //console.log(this.loggedInUser.role)
      this.loggedInUser.role=3
    }

    this.http.post(BACKEND_URL + "/all-users", httpOptions)
    .subscribe(
        (data: any) => {
            if (data) {

              //console.log(data)
              this.users = data


            } else {
                alert("no Data Soz");
            }
        },
        error => {console.error('There was an error:', error);}
    );
  }


  getRoleName(roleNumber: number): string {
    switch (roleNumber) {
      case 1:
        return 'User';
      case 2:
        return 'Admin';
      case 3:
        return 'Super Admin';
      default:
        return 'Unknown Role';
    }
  }


  openEditModal(user: any) {
    this.selectedUser = user;
    //console.log(this.selectedUser)
    this.selectedRole = user.role;
    $('#editUserRoleModal').modal('show');
  }

  updateUserRole() {
    // Convert to number and update the role
    this.selectedUser.role = Number(this.selectedRole);

    console.log(this.selectedUser)


    //--------------------------------------------------------------

    this.http.post(BACKEND_URL+"/update-permission", this.selectedUser).subscribe(
      response => {
          console.log('User details updated on the server.', response);
      },
      error => {
          console.error('There was an error updating the user details on the server.', error);
          alert('Error updating profile. Please try again.');
      }
  )
    //alert('Profile updated!');

  //-------------------------------------------------------------

    // Close the modal

  }

  closeModal(){
    $('#editUserRoleModal').modal('hide');
  }

}

<div class="container mt-5">
  <div style="height: 400px; overflow-y: auto;">
    <table class="table border-0">
      <thead class="bg-primary text-white">
        <tr>
          <th>Profile</th>
          <th>Username</th>
          <th>Email</th>
          <th>Role</th>
          <th>Action</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let user of users">
          <td>
          <div style="width: 40px; height: 40px; border-radius: 50%; background: #ccc;"></div>
          </td>
          <td>{{ user.username }}</td>
          <td>{{ user.email }}</td>
          <td>{{ getRoleName(user.role) }}</td>
          <td>
            <button class="btn btn-sm btn-warning" (click)="openEditModal(user)">Edit User</button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>

<div class="modal fade" id="editUserRoleModal" tabindex="-1" aria-labelledby="editUserRoleLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 *ngIf="selectedUser" class="modal-title" id="editUserRoleLabel">{{selectedUser.username}}</h5>
        <button (click)="closeModal()" type="button" class="close" data-dismiss="modal" aria-label="Close">
        </button>
      </div>
      <div class="modal-body">
        <label for="roleSelect">Select Role:</label>
<select class="form-control" id="roleSelect" [(ngModel)]="selectedRole">
    <option value="1" [disabled]="loggedInUser?.role === 1 || loggedInUser?.role === 2">User</option>
    <option value="2" [disabled]="loggedInUser?.role === 2">Admin</option>
    <option value="3" [disabled]="loggedInUser?.role !== 3">Super Admin</option>
</select>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-primary" (click)="updateUserRole()">Save changes</button>
      </div>
    </div>
  </div>
</div>
