import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-account',
  templateUrl: './account.component.html',
  styleUrls: ['./account.component.css']
})
export class AccountComponent {

  username: string;
  birthdate: string;
  age: number;

  constructor(private router: Router) { 
    this.username = sessionStorage.getItem('username') ?? '';
    this.birthdate = sessionStorage.getItem('userbirthdate') ?? '';
    this.age = Number(sessionStorage.getItem('userage') ?? '0');
}


  logout() {
    // Clear session storage
    sessionStorage.clear();

    // Redirect to login page
    this.router.navigateByUrl('/login');
  }
}
<div class="container mt-4">
    <div class="d-flex justify-content-between align-items-center">
        <h1>Accounts Page</h1>
        <button (click)="logout()" class="btn btn-danger">Logout</button>
    </div>

    <div class="row justify-content-center mt-5">
        <div class="col-6 text-center">
            <img src="https://theskillengineer.com.au/wp-content/uploads/2022/10/Unlocked-logo.png" class="img-fluid mb-4" alt="Sample Image">

            <!-- User details display -->
            <div class="user-details p-4 border rounded">
                <h3>{{username}}</h3>
                <p><strong>Birthdate:</strong> {{birthdate}}</p>
                <p><strong>Age:</strong> {{age}}</p>
            </div>

            <a routerLink="/profile" class="btn btn-info mt-3">Edit Profile</a>
        </div>
    </div>
</div>
