        <!-- Display username at the top -->
        <h3>{{loggedInUser.username}} Channels</h3>
</div>
    <h2>My Channel</h2>
    <button *ngIf= "isAdmin" class="btn btn-secondary" (click)="addChannel('button')">Add Channel</button>
    <!-- <button class="btn btn-secondary" (click)="addChannel()">Delete Channels</button> -->
    <div class="row">
        <div *ngFor="let channel of myChannels" class="col-12 mb-3">
            <div class="card">
                <div class="card-body d-flex justify-content-between align-items-center">
                    <span>{{channel}}</span>
                    <div>
                        <!-- <button class="btn btn-primary mr-2" (click)="addUserToChannel(channel)">Join Channel</button> -->
                        <button class="btn btn-secondary" (click)="removeUserFromChannel(channel)">Leave Channel</button>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>

<div class="container mt-4">
    <h2>Join Channel</h2>
    <div class="row">
        <div *ngFor="let channel of toJoinChannels" class="col-12 mb-3">
            <div class="card">
                <div class="card-body d-flex justify-content-between align-items-center">
                    <span>{{channel}}</span>
                    <div>
                        <button class="btn btn-primary mr-2" (click)="addUserToChannel(channel)">Join Channel</button>
                        <button  *ngIf= "isAdmin" class="btn btn-primary mr-2" (click)="deleteChannel(channel)">Delete</button>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>


    <div class="modal fade" id="addChannelModal" tabindex="-1" aria-labelledby="addGroupLabel" aria-hidden="true">
        <div class="modal-dialog">
          <div class="modal-content">
