import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { User } from 'server/routes/userModel';
import { UploadService } from '../service/upload.service';


const BACKEND_URL = "http://localhost:3000";
const httpOptions = {
  headers: new HttpHeaders({ 'Content-Type': 'application/json' })
};

@Component({
  selector: 'app-create-user',
  templateUrl: './create-user.component.html',
  styleUrls: ['./create-user.component.css']
})

export class CreateUserComponent implements OnInit {

  user: User = new User('', '', 0, '', '', '', 1, [], true);

  allUsernames: string[] = [];
  isUniqueUsername: boolean = true;
  isUniquePassword: boolean = true;
  isUniqueEmail:boolean = true;

  constructor(private router: Router, private http: HttpClient, private uploadService: UploadService) {}

  ngOnInit(): void {
    this.isUniqueEmail=true; 
    this.http.get<string[]>(BACKEND_URL + "/usernames").subscribe(usernames => {
      this.allUsernames = usernames;
      console.log(this.allUsernames);
    });
  }

  onUsernameInput() {
    // Check if username is unique while the user is typing
    this.isUniqueUsername = !this.allUsernames.includes(this.user.username);
  }

  onCreate() {
    if (this.user.password !== this.user.pwdconfirm) {
      //alert('Passwords do not match!');  // Notify the user
      this.isUniquePassword=false; 
      return;
    }

    const userToSend = { ...this.user };

    this.http.post(BACKEND_URL + "/addUser", userToSend, httpOptions).subscribe(
      (response: any) => {
        if (response && response.success) {
          this.router.navigateByUrl('login');
        } else {
          alert("Email already exists!");
        }
      },
      error => {
        console.error('There was an error during user creation:', error);
      if (error && error.error && error.error.message) {
        console.error('Server response:', error.error.message);
        this.isUniqueEmail=false
      }
    }
    );
  }

  onUpload(event) {
    const file = event.target.files[0];
    if (file) {
      const formData = new FormData();
      formData.append('file', file, file.name);

      this.uploadService.uploadFile(formData).subscribe(response => {
        console.log('Upload successful', response);
      }, error => {
        console.error('Upload error', error);
      });
    }
  }

}



<div class="container mt-5">
    <h2 class="mb-4">Create Account</h2>
    <form (ngSubmit)="onCreate()">
        <div class="mb-3">

            <label for="username" class="form-label">Username</label>

            <input type="text"  class="form-control" id="username"[(ngModel)]="user.username" name="username" required (input)="onUsernameInput()" [ngClass]="{
                'is-unique': user.username && isUniqueUsername, 
                'not-unique': user.username && !isUniqueUsername}">
        </div>

        <div *ngIf="!isUniqueUsername" class="invalid-feedback d-block">
            User already exists, Please choose a new username
        </div>


        <div class="mb-3">
            <label for="password" class="form-label">Password</label>
            <input type="password" class="form-control" id="password" [(ngModel)]="user.password" name="password" required [ngClass]="{
                'not-unique': user.password && !isUniquePassword}">
        </div>
        <div class="mb-3">
            <label for="password2" class="form-label">Confirm Password</label>
            <input type="password" class="form-control" id="pwdconfirm" [(ngModel)]="user.pwdconfirm" name="pwdconfirm" required [ngClass]="{
                'not-unique': user.pwdconfirm && !isUniquePassword}">
        </div>

        <div *ngIf="!isUniquePassword" class="invalid-feedback d-block">
            Passwords do not match, Please try again. 
        </div>
        <div class="mb-3">
            <label for="email" class="form-label">Email</label>
            <input type="email" class="form-control" id="email" [(ngModel)]="user.email" name="email" required [ngClass]="{
                'not-unique': user.email && !isUniqueEmail}">
        </div>
        <div *ngIf="!isUniqueEmail" class="invalid-feedback d-block">
            Email Already Exists, Please try again.
        </div>
        <div class="mb-3">
            <label for="birthday" class="form-label">Birthday</label>
            <input type="date" class="form-control" id="birthday" [(ngModel)]="user.birthdate" name="birthday" required>
        </div>

        <div class="mb-3">
            <label for="birthday" class="form-label">Birthday</label>
            <input type="date" class="form-control" id="birthday" [(ngModel)]="user.birthdate" name="birthday" required>
        </div>

        <h1>upload profile picture</h1>
            <form action="/" method="post" enctype="multipart/form-data">
              <input type="file" name="file">
              <button type="submit">Upload</button>

            </form>


        <input type="hidden" [(ngModel)]="user.role" name="roles" value="[1]">
        <input type="hidden" [(ngModel)]="user.group" name="groups" value="[1]">
        <button type="submit" class="btn btn-primary">Create Account</button>


    </form>
</div>
